INSERT INTO habitat (habitat_id, habitat_type, habitat_description) VALUES (2, 'FOREST', 'Include areas covered mainly with trees, show some layering and undergrowth. Recently disturbed forestland (cutover or wildfire) that currently has no forest cover is considered forest habitat as it is expected to revert to forest.');
INSERT INTO habitat (habitat_id, habitat_type, habitat_description) VALUES (3, 'NON-FOREST', 'Include areas covered with grass, shrubs, scrub, or a combination of several vegetation types. Generally, non-forest habitats show a single layer and, when present, a short undergrowth.');
INSERT INTO habitat (habitat_id, habitat_type, habitat_description) VALUES (4, 'MICRO-HABITAT', 'Include areas with a different composition, structure, or substrate that differs from the more extensive habitat. Microhabitats are diverse and generally are composed of a plant species, a plant community, or abiotic elements, all nested within a more extensive habitat.');
